select distinct top 10 v_R_System.NetbiosName, v_R_System.OperatingSystemNameandVersion, v_R_System.IPAddresses, v_R_System.MACAddresses, v_R_System.IPv6Addresses, v_R_System.IsAssignedToUser, v_R_System.ResourceType, v_GS_BASEBOARD.SerialNumber, v_GS_PHYSICAL_MEMORY.Speed, v_GS_DISK.Size, v_GS_NETWORK_ADAPTER.MACAddress from  v_R_System inner join v_GS_BASEBOARD on v_GS_BASEBOARD.ResourceId = v_R_System.ResourceId inner join v_GS_PHYSICAL_MEMORY on v_GS_PHYSICAL_MEMORY.ResourceId = v_R_System.ResourceId inner join v_GS_DISK on v_GS_DISK.ResourceId = v_R_System.ResourceId inner join v_GS_NETWORK_ADAPTER on v_GS_NETWORK_ADAPTER.ResourceId = v_R_System.ResourceId order by v_R_System.CreationDate